// ì „ì—­ ì„¸ì…˜ ì €ì¥ì†Œ - ì—¬ëŸ¬ APIê°€ ê³µìœ 
class SessionStore {
  private static instance: SessionStore
  private sessions: Map<string, SessionData>

  private constructor() {
    this.sessions = new Map()
  }

  public static getInstance(): SessionStore {
    if (!SessionStore.instance) {
      SessionStore.instance = new SessionStore()
    }
    return SessionStore.instance
  }

  public set(sessionId: string, session: SessionData): void {
    this.sessions.set(sessionId, session)
    console.log(`ì„¸ì…˜ ì €ì¥: ${sessionId}, í˜„ì¬ ì´ ${this.sessions.size}ê°œ ì„¸ì…˜`)
  }

  public get(sessionId: string): SessionData | undefined {
    const session = this.sessions.get(sessionId)
    console.log(`ì„¸ì…˜ ì¡°íšŒ: ${sessionId} â†’ ${session ? 'ì°¾ìŒ' : 'ì—†ìŒ'}, í˜„ì¬ ì´ ${this.sessions.size}ê°œ ì„¸ì…˜`)
    return session
  }

  public delete(sessionId: string): boolean {
    const result = this.sessions.delete(sessionId)
    console.log(`ì„¸ì…˜ ì‚­ì œ: ${sessionId} â†’ ${result ? 'ì„±ê³µ' : 'ì‹¤íŒ¨'}, í˜„ì¬ ì´ ${this.sessions.size}ê°œ ì„¸ì…˜`)
    return result
  }

  public has(sessionId: string): boolean {
    return this.sessions.has(sessionId)
  }

  public getAllSessions(): SessionData[] {
    return Array.from(this.sessions.values())
  }

  public clearExpired(): number {
    const now = new Date()
    let cleared = 0
    const expiredSessions: string[] = []

    // ë§Œë£Œëœ ì„¸ì…˜ ID ìˆ˜ì§‘
    this.sessions.forEach((session, sessionId) => {
      if (new Date(session.expiresAt) < now) {
