    }

    // ì„¸ì…˜ í™•ì¸ - ìƒˆë¡œìš´ ì €ì¥ì†Œ ì‚¬ìš©
    const session = sessionStore.get(sessionId)
    if (!session) {
      return NextResponse.json(
        { error: 'ìœ íš¨í•˜ì§€ ì•Šì€ ì„¸ì…˜ì…ë‹ˆë‹¤.' },
        { status: 404 }
      )
    }

    // ì„¸ì…˜ ë§Œë£Œ í™•ì¸
    if (new Date() > new Date(session.expiresAt)) {
      return NextResponse.json(
